import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./Community.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faQuestionCircle } from '@fortawesome/free-solid-svg-icons';

function CommunityDashboard() {
  const [communities, setCommunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scrolling, setScrolling] = useState(false);

  useEffect(() => {
    async function fetchCommunities() {
      try {
        const response = await axios.get('http://192.168.1.2:8000/get_all_communities/');
        setCommunities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching communities:', error);
      }
    }

    fetchCommunities();
  }, []);

  const handleDeleteCommunity = async (communityId) => {
    try {
      await axios.delete(`http://your-django-api-url/communities/${communityId}`);
      // Update state to reflect the deletion
      setCommunities(communities.filter((community) => community.id !== communityId));
    } catch (error) {
      console.error('Error deleting community:', error);
    }
  };

  const handleToggleScroll = () => {
    if (scrolling) {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    } else {
      window.scrollTo({
        top: document.documentElement.scrollHeight,
        behavior: 'smooth'
      });
    }
    setScrolling(!scrolling);
  };

  return (
    <div className='productdash'>
      <h1>Community Dashboard</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {communities['communities'].map((community) => (
            <li key={community.id}>
              {community.name}
              <button onClick={() => handleDeleteCommunity(community.id)}>
                <FontAwesomeIcon icon={faTrash} border={false} />
              </button>
              <button onClick={handleToggleScroll}>
                <FontAwesomeIcon icon={faQuestionCircle} border={false} />
              </button>
              <br />
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CommunityDashboard;
