import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./Community.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMinus, faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

function CommunityDashboard() {
  const [communities, setCommunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openDetailsId, setOpenDetailsId] = useState(null);

  useEffect(() => {
    async function fetchCommunities() {
      try {
        const response = await axios.get('http://192.168.1.236:8000/get_all_communities/');
        setCommunities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching communities:', error);
      }
    }

    fetchCommunities();
  }, []);

  const handleDeleteCommunity = async (communityId) => {
    try {
      await axios.delete(`http://your-django-api-url/communities/${communityId}`);
      // Update state to reflect the deletion
      setCommunities(communities.filter((community) => community.id !== communityId));
    } catch (error) {
      console.error('Error deleting community:', error);
    }
  };

  const toggleDetails = (communityId) => {
    setOpenDetailsId(openDetailsId === communityId ? null : community);
  };

  return (
    <div className='productdash'>
      <h1>Community Dashboard</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {communities['communities'].map((community) => (
            <li>
              <div className='community'>
                <div>{community}</div>
                <div className='communityIcon'>
                  <FontAwesomeIcon icon={faTrash} onClick={() => handleDeleteCommunity(community.id)} />
                  <FontAwesomeIcon icon={openDetailsId === community.id ? faMinus : faPlus} onClick={() => toggleDetails(community)} />
                </div>
              </div>
              {openDetailsId === community && (
                <div>
                  Hello
                  {/* Afficher les d√©tails ici */}
                </div>
              )}
              <br />
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CommunityDashboard;
