import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./Community.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInfoCircle, faTrash } from '@fortawesome/free-solid-svg-icons';

function CommunityDashboard() {
  const [communities, setCommunities] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchCommunities() {
      try {
        const response = await axios.get('http://192.168.1.2:8000/get_all_communities/');
        setCommunities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching communities:', error);
      }
    }

    fetchCommunities();
  }, []);

  const handleDeleteCommunity = async (communityId) => {
    try {
      await axios.delete(`http://your-django-api-url/communities/${communityId}`);
      // Update state to reflect the deletion
      setCommunities(communities.filter((community) => community.id !== communityId));
    } catch (error) {
      console.error('Error deleting community:', error);
    }
  };

  return (
    <div className='productdash'>
      <h1>Community Dashboard</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {communities['communities'].map((community) => (
            <li>
              {community}
              <button onClick={() => handleDeleteCommunity(community.id)}>
              <svg aria-hidden="true" focusable="false" data-prefix="fas" data-icon="trash" class="svg-inline--fa fa-trash " role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512"><path fill="currentColor" d="M135.2 17.7L128 32H32C14.3 32 0 46.3 0 64S14.3 96 32 96H416c17.7 0 32-14.3 32-32s-14.3-32-32-32H320l-7.2-14.3C307.4 6.8 296.3 0 284.2 0H163.8c-12.1 0-23.2 6.8-28.6 17.7zM416 128H32L53.2 467c1.6 25.3 22.6 45 47.9 45H346.9c25.3 0 46.3-19.7 47.9-45L416 128z"></path></svg>              </button>
              <button>
                <FontAwesomeIcon icon={faInfoCircle} border={false} />
              </button>
              <br />
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CommunityDashboard;
