import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./Community.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMinus, faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';

function CommunityDashboard() {
  const [communities, setCommunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openDetailsId, setOpenDetailsId] = useState(null);
  const [communityMembers, setCommunityMembers] = useState([]);

  useEffect(() => {
    async function fetchCommunities() {
      try {
        const response = await axios.get('http://192.168.1.236:8000/get_all_communities/');
        setCommunities(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching communities:', error);
      }
    }

    fetchCommunities();
  }, []);

  const handleDeleteCommunity = async (communityName) => {
    try {
      await axios.delete(`http://your-django-api-url/communities/${communityName}`);
      // Update state to reflect the deletion
      setCommunities(communities.filter((community) => community !== communityName));
    } catch (error) {
      console.error('Error deleting community:', error);
    }
  };

  const toggleDetails = async (communityName) => {
    if (openDetailsId === communityName) {
      setOpenDetailsId(null);
      setCommunityMembers([]);
    } else {
      try {
        const response = await axios.get('http://192.168.1.236:8000/get_community_members/', {
          params: {
            community_name: communityName
          }
        });
        setCommunityMembers(response.data);
        setOpenDetailsId(communityName);
      } catch (error) {
        console.error('Error fetching community members:', error);
      }
    }
  };

  return (
    <div className='productdash'>
      <h1>Community Dashboard</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {communities['communities'].map((community) => (
            <li key={community}>
              <div className='community'>
                <div>{community}</div>
                <div className='communityIcon'>
                  <FontAwesomeIcon icon={faTrash} onClick={() => handleDeleteCommunity(community)} />
                  <FontAwesomeIcon icon={openDetailsId === community ? faMinus : faPlus} onClick={() => toggleDetails(community)} />
                </div>
              </div>
              {openDetailsId === community && (
                <div>
                  <h2>Community Members</h2>
                  <ul>
                    {communityMembers.map((member, index) => (
                      <li key={index}>{member.username}</li>
                    ))}
                  </ul>
                </div>
              )}
              <br />
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default CommunityDashboard;
